                  key={t.id}
                  class={`pressy-theme-btn ${
                    activeTheme === t.id ? "pressy-theme-btn--active" : ""
                  }`}
                  onClick={(e: MouseEvent) => {
                    e.stopPropagation();
                    handleThemeChange(t.id);
