/* ── Page footer ───────────────────────────────────────────── */
.pressy-page-footer {
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 0.5rem 1.5rem calc(1rem + env(safe-area-inset-bottom, 0px));
  text-align: center;
  user-select: none;
  transform: translateY(100%);
  opacity: 0;
  transition: transform 0.25s ease, opacity 0.25s ease;
  z-index: 15;
  background: var(--color-bg, #ffffff);
}

