 22→// IndexedDB for reading progress
 23→const DB_NAME = 'pressy'
 24→const DB_VERSION = 1
 25→const PROGRESS_STORE = 'reading-progress'
 26→const UNLOCKS_STORE = 'unlocks'
 27→
 28→let db: IDBDatabase | null = null
 29→
 30→async function initDB(): Promise<IDBDatabase> {
 31→  if (db) return db
 32→
 33→  return new Promise((resolve, reject) => {
 34→    const request = indexedDB.open(DB_NAME, DB_VERSION)
 35→
 36→    request.onerror = () => reject(request.error)
 37→    request.onsuccess = () => {
 38→      db = request.result
 39→      resolve(db)
 40→    }
 41→
 42→    request.onupgradeneeded = (event) => {
 43→      const database = (event.target as IDBOpenDBRequest).result
 44→      if (!database.objectStoreNames.contains(PROGRESS_STORE)) {
 45→        database.createObjectStore(PROGRESS_STORE, { keyPath: 'chapterSlug' })
 46→      }
 47→      if (!database.objectStoreNames.contains(UNLOCKS_STORE)) {
 48→        database.createObjectStore(UNLOCKS_STORE, { keyPath: 'bookSlug' })
 49→      }
 50→    }
 51→  })
 52→}
 53→
 54→export async function saveReadingProgress(progress: ReadingProgress): Promise<void> {
 55→  const database = await initDB()
 56→  return new Promise((resolve, reject) => {
 57→    const tx = database.transaction(PROGRESS_STORE, 'readwrite')
 58→    const store = tx.objectStore(PROGRESS_STORE)
 59→    const request = store.put(progress)
 60→    request.onsuccess = () => resolve()
 61→    request.onerror = () => reject(request.error)
 62→  })
 63→}
 64→
 65→export async function getReadingProgress(chapterSlug: string): Promise<ReadingProgress | null> {
 66→  const database = await initDB()
 67→  return new Promise((resolve, reject) => {
 68→    const tx = database.transaction(PROGRESS_STORE, 'readonly')
 69→    const store = tx.objectStore(PROGRESS_STORE)
 70→    const request = store.get(chapterSlug)
 71→    request.onsuccess = () => resolve(request.result || null)
 72→    request.onerror = () => reject(request.error)
 73→  })
 74→}
 75→
 76→export async function getAllReadingProgress(): Promise<ReadingProgress[]> {
 77→  const database = await initDB()
 78→  return new Promise((resolve, reject) => {
 79→    const tx = database.transaction(PROGRESS_STORE, 'readonly')
 80→    const store = tx.objectStore(PROGRESS_STORE)
 81→    const request = store.getAll()
 82→    request.onsuccess = () => resolve(request.result || [])
 83→    request.onerror = () => reject(request.error)
 84→  })
 85→}
 86→
 87→export async function isBookUnlocked(bookSlug: string): Promise<boolean> {
 88→  const database = await initDB()
 89→  return new Promise((resolve, reject) => {
 90→    const tx = database.transaction(UNLOCKS_STORE, 'readonly')
 91→    const store = tx.objectStore(UNLOCKS_STORE)
 92→    const request = store.get(bookSlug)
 93→    request.onsuccess = () => resolve(!!request.result)
 94→    request.onerror = () => reject(request.error)
 95→  })
 96→}
 97→
 98→export async function unlockBook(bookSlug: string, orderId?: string): Promise<void> {
 99→  const database = await initDB()
100→  return new Promise((resolve, reject) => {
101→    const tx = database.transaction(UNLOCKS_STORE, 'readwrite')
102→    const store = tx.objectStore(UNLOCKS_STORE)
103→    const request = store.put({ bookSlug, orderId, unlockedAt: Date.now() })
104→    request.onsuccess = () => resolve()
105→    request.onerror = () => reject(request.error)
106→  })
107→}
