244→              </a>
245→            ))}
246→          </nav>
247→        </section>
248→      )}
249→      <style>{HOME_STYLES}</style>
250→    </div>
251→  )
252→}
253→
254→// Calculates global book progress % from word counts + saved progress data
255→function calculateBookProgressPercent(
256→  book: Book,
257→  currentChapterSlug: string,
258→  currentPage: number,
259→  currentTotalPages: number,
260→  allProgress: ReadingProgress[],
261→): number {
262→  const totalWords = book.chapters.reduce((sum, ch) => sum + (ch.wordCount || 0), 0)
263→  if (totalWords === 0) return 0
264→
265→  const progressMap = new Map(allProgress.map((p) => [p.chapterSlug, p]))
266→  let wordsRead = 0
267→
268→  for (const ch of book.chapters) {
269→    const chapterWords = ch.wordCount || 0
270→    if (ch.slug === currentChapterSlug) {
271→      // Use live page position for current chapter
272→      if (currentTotalPages > 0) {
273→        wordsRead += (currentPage / Math.max(1, currentTotalPages - 1)) * chapterWords
274→      }
275→    } else {
276→      const progress = progressMap.get(ch.slug)
277→      if (!progress) continue
278→      if (progress.totalPages > 0 && progress.page >= progress.totalPages - 1) {
279→        wordsRead += chapterWords
280→      } else if (progress.page > 0 && progress.totalPages > 0) {
281→        wordsRead += (progress.page / progress.totalPages) * chapterWords
282→      }
283→    }
284→  }
285→
286→  return Math.min(100, (wordsRead / totalWords) * 100)
287→}
288→
289→// Wrapper component that provides progress callbacks and computes global progress
290→function ChapterReaderWithProgress({
291→  book,
292→  chapterSlug,
293→  chapter,
294→  prevChapter,
295→  nextChapter,
296→  paginationMode,
297→  Content,
298→  chapterMapData,
299→}: {
300→  book: Book
301→  chapterSlug: string
302→  chapter?: Chapter
303→  prevChapter?: { slug: string; title: string }
304→  nextChapter?: { slug: string; title: string }
305→  paginationMode?: 'scroll' | 'paginated'
306→  Content: ComponentType<{ components?: Record<string, unknown> }>
307→  chapterMapData?: ChapterMapData
308→}) {
309→  const [bookProgressPercent, setBookProgressPercent] = useState<number | undefined>(undefined)
310→
311→  // Load initial global book progress
312→  useEffect(() => {
313→    getAllReadingProgress().then((allProgress) => {
314→      const percent = calculateBookProgressPercent(book, chapterSlug, 0, 0, allProgress)
315→      setBookProgressPercent(percent)
316→    })
317→  }, [book, chapterSlug])
318→
319→  const handleSaveProgress = (data: ProgressData) => {
320→    saveReadingProgress({
321→      chapterSlug,
322→      page: data.page,
323→      totalPages: data.totalPages,
324→      scrollPosition: data.scrollPosition,
325→      timestamp: Date.now(),
326→    })
327→
328→    // Update global book progress with live page position
329→    if (data.totalPages > 0) {
330→      getAllReadingProgress().then((allProgress) => {
331→        const percent = calculateBookProgressPercent(
332→          book, chapterSlug, data.page, data.totalPages, allProgress,
333→        )
334→        setBookProgressPercent(percent)
335→      })
336→    }
337→  }
338→
339→  const handleRestoreProgress = async (): Promise<ProgressData | null> => {
340→    const progress = await getReadingProgress(chapterSlug)
341→    if (!progress) return null
342→    return {
343→      page: progress.page,
344→      totalPages: progress.totalPages,
345→      scrollPosition: progress.scrollPosition,
346→    }
347→  }
348→
349→  const handleChapterChange = (slug: string, page: number, chapterTotalPages: number) => {
350→    // Save progress for the chapter being left (mark as complete)
351→    saveReadingProgress({
352→      chapterSlug: slug,
353→      page,
354→      totalPages: chapterTotalPages,
355→      scrollPosition: 0,
356→      timestamp: Date.now(),
357→    })
358→
359→    // Update global book progress
360→    getAllReadingProgress().then((allProgress) => {
361→      const percent = calculateBookProgressPercent(book, slug, page, chapterTotalPages, allProgress)
362→      setBookProgressPercent(percent)
363→    })
364→  }
365→
366→  // Prefetch previous chapter HTML for fast backward navigation
367→  useEffect(() => {
368→    if (prevChapter) {
369→      const link = document.createElement('link')
370→      link.rel = 'prefetch'
371→      link.href = prevChapter.slug
372→      document.head.appendChild(link)
373→      return () => { document.head.removeChild(link) }
374→    }
375→  }, [prevChapter])
376→
377→  return (
378→    <Reader
379→      title={chapter?.title || chapterSlug}
380→      chapterSlug={chapterSlug}
381→      prevChapter={prevChapter}
382→      nextChapter={nextChapter}
383→      paginationMode={paginationMode}
384→      onSaveProgress={handleSaveProgress}
385→      onRestoreProgress={handleRestoreProgress}
386→      bookProgressPercent={bookProgressPercent}
387→      initialContent={Content}
388→      chapterMapData={chapterMapData}
389→      currentChapterSlug={chapterSlug}
390→      allChapters={book.chapters.map(ch => ({ slug: ch.slug, title: ch.title, wordCount: ch.wordCount }))}
391→      bookBasePath={`${basePath}/books/${book.slug}`}
392→      onChapterChange={handleChapterChange}
393→      mdxComponents={useMDXComponents()}
394→    >
395→      <Content components={useMDXComponents()} />
396→    </Reader>
397→  )
398→}
399→
400→function renderChapterPage(
401→  manifest: ContentManifest,
402→  route: string,
403→  Content: ComponentType,
404→  paginationMode?: 'scroll' | 'paginated',
405→  chapterMapData?: ChapterMapData,
406→) {
407→  const parts = route.split('/')
408→  const bookSlug = parts[2]
409→  const chapterSlug = parts[3]
410→  const book = manifest.books.find((b: Book) => b.slug === bookSlug)
411→  const chapterIdx = book ? book.chapters.findIndex((c: Chapter) => c.slug === chapterSlug) : -1
412→  const chapter = book?.chapters[chapterIdx]
413→  const chapterPath = (ch: Chapter) => `${basePath}/books/${bookSlug}/${ch.slug}`
414→  const prevChapter = book && chapterIdx > 0
415→    ? { slug: chapterPath(book.chapters[chapterIdx - 1]), title: book.chapters[chapterIdx - 1].title }
416→    : book
417→      ? { slug: `${basePath}/books/${bookSlug}`, title: book.metadata.title }
418→      : undefined
419→  const nextChapter = book && chapterIdx >= 0 && chapterIdx < book.chapters.length - 1
420→    ? { slug: chapterPath(book.chapters[chapterIdx + 1]), title: book.chapters[chapterIdx + 1].title }
421→    : book
422→      ? { slug: `${basePath}/books/${bookSlug}`, title: book.metadata.title }
423→      : undefined
424→
425→  const MDXContent = Content as ComponentType<{ components?: Record<string, unknown> }>
426→
427→  if (!book) {
428→    return (
429→      <Reader
430→        title={chapter?.title || chapterSlug}
431→        prevChapter={prevChapter}
432→        nextChapter={nextChapter}
433→        paginationMode={paginationMode}
434→      >
435→        <MDXContent components={useMDXComponents()} />
436→      </Reader>
437→    )
438→  }
439→
440→  return (
441→    <ChapterReaderWithProgress
442→      book={book}
443→      chapterSlug={chapterSlug}
444→      chapter={chapter}
445→      prevChapter={prevChapter}
446→      nextChapter={nextChapter}
447→      paginationMode={paginationMode}
448→      Content={MDXContent}
449→      chapterMapData={chapterMapData}
450→    />
451→  )
452→}
453→
454→function renderArticlePage(
455→  manifest: ContentManifest,
456→  route: string,
457→  Content: ComponentType,
458→) {
459→  const articleSlug = route.split('/')[2]
460→  const article = manifest.articles.find((a: Article) => a.slug === articleSlug)
461→
462→  const MDXContent = Content as ComponentType<{ components?: Record<string, unknown> }>
463→
464→  return (
465→    <Reader title={article?.metadata.title || articleSlug}>
466→      <MDXContent components={useMDXComponents()} />
467→    </Reader>
468→  )
469→}
470→
471→// ── Styles ──────────────────────────────────────────────────
472→
473→const HOME_STYLES = `
474→  .pressy-home {
475→    max-width: 65ch;
476→    margin: 0 auto;
477→    padding: 2rem 1.5rem;
478→    font-family: var(--font-body, Georgia, 'Times New Roman', serif);
479→    color: var(--color-text, #1a1a1a);
480→  }
481→  .pressy-home-header {
482→    margin-bottom: 3rem;
483→    text-align: center;
484→  }
485→  .pressy-home-header h1 {
486→    font-size: 2rem;
487→    margin-bottom: 0.5rem;
488→  }
489→  .pressy-home-author {
490→    font-style: italic;
491→    color: var(--color-text-muted, #666);
492→  }
493→  .pressy-home-desc {
494→    color: var(--color-text-muted, #666);
495→    line-height: 1.6;
496→    max-width: 50ch;
497→    margin: 0.5rem auto 0;
498→  }
499→  .pressy-home-section {
500→    margin-bottom: 2rem;
501→  }
502→  .pressy-home-section h2 {
503→    font-size: 1.25rem;
504→    margin-bottom: 1rem;
505→    border-bottom: 1px solid var(--color-border, #e5e5e5);
506→    padding-bottom: 0.5rem;
507→  }
508→  .pressy-chapter-list {
509→    display: flex;
510→    flex-direction: column;
511→    gap: 0.25rem;
512→  }
513→  .pressy-chapter-link {
514→    display: flex;
515→    gap: 0.75rem;
516→    padding: 0.75rem 1rem;
517→    text-decoration: none;
518→    color: var(--color-text, #1a1a1a);
519→    border-radius: 0.5rem;
520→    transition: background 0.15s;
521→  }
522→  .pressy-chapter-link:hover {
523→    background: var(--color-bg-subtle, #f5f5f5);
524→  }
525→  .pressy-chapter-order {
526→    color: var(--color-text-muted, #666);
527→    min-width: 2ch;
528→  }
529→`
530→
531→// ── Main hydration function ─────────────────────────────────
532→
533→interface HydrateData {
534→  route: string
535→  routeType: string
536→  manifest: ContentManifest
537→  pagination?: PaginationConfig
538→}
539→
540→// Compute the base path by comparing the known route to the actual URL
541→// e.g. URL="/pressy/pr-preview/pr-4/books/flatland" with route="/books/flatland"
542→// yields basePath="/pressy/pr-preview/pr-4"
543→function getBasePath(route: string): string {
544→  const pathname = window.location.pathname
545→  // Remove trailing slash for comparison
546→  const cleanPath = pathname.endsWith('/') ? pathname.slice(0, -1) : pathname
547→  const cleanRoute = route.endsWith('/') ? route.slice(0, -1) : route
548→  if (cleanRoute === '' || cleanRoute === '/') {
549→    // Home page: strip /index.html or trailing slash
550→    return cleanPath.replace(/\/index\.html$/, '') || ''
551→  }
552→  const idx = cleanPath.indexOf(cleanRoute)
553→  if (idx > 0) {
554→    return cleanPath.slice(0, idx)
555→  }
556→  return ''
557→}
558→
559→let basePath = ''
560→
561→export function hydrate(data: HydrateData, Content?: ComponentType, chapterMapData?: ChapterMapData): void {
562→  basePath = getBasePath(data.route)
563→  currentRoute.value = data.route
564→
565→  // Initialize
566→  loadTheme()
567→  setupOfflineDetection()
568→  initDB()
569→
570→  // Register service worker for PWA offline support
571→  registerServiceWorker(basePath)
572→
573→  // Handle popstate for back/forward navigation
574→  window.addEventListener('popstate', () => {
575→    currentRoute.value = window.location.pathname
576→  })
577→
578→  // Handle client-side link clicks
579→  // For now, allow normal browser navigation for cross-page links
580→  // so each page loads its own HTML with the correct MDX content.
581→  // Only intercept hash links for in-page navigation.
582→  document.addEventListener('click', (e) => {
583→    const link = (e.target as HTMLElement).closest('a')
584→    if (!link) return
585→
586→    const href = link.getAttribute('href')
587→    if (!href) return
588→
589→    // Let external and full-page links navigate normally
590→    if (href.startsWith('http') || href.startsWith('#')) return
591→
592→    // Same-path hash links: handle client-side
593→    if (href.startsWith(data.route + '#')) {
594→      e.preventDefault()
595→      const hash = href.slice(href.indexOf('#'))
596→      const target = document.querySelector(hash)
597→      if (target) target.scrollIntoView({ behavior: 'smooth' })
598→      return
599→    }
600→
601→    // Cross-page links: let the browser navigate normally
602→    // (each page is a full server-rendered HTML document)
603→  })
604→
605→  // Render the app
606→  let page: VNode
607→  switch (data.routeType) {
608→    case 'home':
609→      page = renderHomePage(data.manifest)
610→      break
611→    case 'book': {
612→      const bookSlug = data.route.split('/')[2]
613→      const book = data.manifest.books.find((b: Book) => b.slug === bookSlug)
614→      page = book ? renderBookPage(book) : <div>Book not found</div>
615→      break
616→    }
617→    case 'chapter':
618→      page = Content
619→        ? renderChapterPage(data.manifest, data.route, Content, data.pagination?.defaultMode, chapterMapData)
620→        : <div>Loading...</div>
621→      break
622→    case 'article':
623→      page = Content
624→        ? renderArticlePage(data.manifest, data.route, Content)
625→        : <div>Loading...</div>
626→      break
627→    case 'books':
628→      page = renderHomePage(data.manifest)
629→      break
630→    case 'articles':
631→      page = renderHomePage(data.manifest)
632→      break
633→    default:
634→      page = <div>Page not found</div>
635→  }
636→
637→  render(page, document.getElementById('app')!)
638→}
639→
640→// Export for use in components
641→export { signal, effect }
642→

