413→function renderChapterPage(
414→  manifest: ContentManifest,
415→  route: string,
416→  Content: ComponentType,
417→  paginationMode?: 'scroll' | 'paginated',
418→  chapterMapData?: ChapterMapData,
419→) {
420→  const parts = route.split('/')
421→  const bookSlug = parts[2]
422→  const chapterSlug = parts[3]
423→  const book = manifest.books.find((b: Book) => b.slug === bookSlug)
424→  const chapterIdx = book ? book.chapters.findIndex((c: Chapter) => c.slug === chapterSlug) : -1
425→  const chapter = book?.chapters[chapterIdx]
426→  const chapterPath = (ch: Chapter) => `${basePath}/books/${bookSlug}/${ch.slug}`
427→  const prevChapter = book && chapterIdx > 0
428→    ? { slug: chapterPath(book.chapters[chapterIdx - 1]), title: book.chapters[chapterIdx - 1].title }
429→    : book
430→      ? { slug: `${basePath}/books/${bookSlug}`, title: book.metadata.title }
431→      : undefined
432→  const nextChapter = book && chapterIdx >= 0 && chapterIdx < book.chapters.length - 1
433→    ? { slug: chapterPath(book.chapters[chapterIdx + 1]), title: book.chapters[chapterIdx + 1].title }
434→    : book
435→      ? { slug: `${basePath}/books/${bookSlug}`, title: book.metadata.title }
436→      : undefined
437→
438→  const MDXContent = Content as ComponentType<{ components?: Record<string, unknown> }>
439→
440→  if (!book) {
441→    return (
442→      <Reader
443→        title={chapter?.title || chapterSlug}
444→        prevChapter={prevChapter}
445→        nextChapter={nextChapter}
446→        paginationMode={paginationMode}
447→      >
448→        <MDXContent components={useMDXComponents()} />
449→      </Reader>
450→    )
451→  }
452→
453→  return (
454→    <ChapterReaderWithProgress
455→      book={book}
456→      chapterSlug={chapterSlug}
457→      chapter={chapter}
458→      prevChapter={prevChapter}
459→      nextChapter={nextChapter}
460→      paginationMode={paginationMode}
461→      Content={MDXContent}
462→      chapterMapData={chapterMapData}
463→    />
464→  )
465→}
