545→
546→interface HydrateData {
547→  route: string
548→  routeType: string
549→  manifest: ContentManifest
550→  pagination?: PaginationConfig
551→}
552→
553→// Compute the base path by comparing the known route to the actual URL
554→// e.g. URL="/pressy/pr-preview/pr-4/books/flatland" with route="/books/flatland"
555→// yields basePath="/pressy/pr-preview/pr-4"
556→function getBasePath(route: string): string {
557→  const pathname = window.location.pathname
558→  // Remove trailing slash for comparison
559→  const cleanPath = pathname.endsWith('/') ? pathname.slice(0, -1) : pathname
560→  const cleanRoute = route.endsWith('/') ? route.slice(0, -1) : route
561→  if (cleanRoute === '' || cleanRoute === '/') {
562→    // Home page: strip /index.html or trailing slash
563→    return cleanPath.replace(/\/index\.html$/, '') || ''
564→  }
565→  const idx = cleanPath.indexOf(cleanRoute)
566→  if (idx > 0) {
567→    return cleanPath.slice(0, idx)
568→  }
569→  return ''
570→}
571→
572→let basePath = ''
573→
574→export function hydrate(data: HydrateData, Content?: ComponentType, chapterMapData?: ChapterMapData): void {
575→  basePath = getBasePath(data.route)
576→  currentRoute.value = data.route
577→
578→  // Initialize
579→  loadTheme()
580→  setupOfflineDetection()
581→  initDB()
582→
583→  // Register service worker for PWA offline support
584→  registerServiceWorker(basePath)
585→
586→  // Handle popstate for back/forward navigation
587→  window.addEventListener('popstate', () => {
588→    currentRoute.value = window.location.pathname
589→  })
590→
591→  // Handle client-side link clicks
592→  // For now, allow normal browser navigation for cross-page links
593→  // so each page loads its own HTML with the correct MDX content.
594→  // Only intercept hash links for in-page navigation.
595→  document.addEventListener('click', (e) => {
596→    const link = (e.target as HTMLElement).closest('a')
597→    if (!link) return
598→
599→    const href = link.getAttribute('href')
600→    if (!href) return
601→
602→    // Let external and full-page links navigate normally
603→    if (href.startsWith('http') || href.startsWith('#')) return
604→
605→    // Same-path hash links: handle client-side
606→    if (href.startsWith(data.route + '#')) {
607→      e.preventDefault()
608→      const hash = href.slice(href.indexOf('#'))
609→      const target = document.querySelector(hash)
610→      if (target) target.scrollIntoView({ behavior: 'smooth' })
611→      return
612→    }
613→
614→    // Cross-page links: let the browser navigate normally
615→    // (each page is a full server-rendered HTML document)
616→  })
617→
618→  // Render the app
619→  let page: VNode
620→  switch (data.routeType) {
621→    case 'home':
622→      page = renderHomePage(data.manifest)
623→      break
624→    case 'book': {
625→      const bookSlug = data.route.split('/')[2]
626→      const book = data.manifest.books.find((b: Book) => b.slug === bookSlug)
627→      page = book ? renderBookPage(book) : <div>Book not found</div>
628→      break
629→    }
630→    case 'chapter':
631→      page = Content
632→        ? renderChapterPage(data.manifest, data.route, Content, data.pagination?.defaultMode, chapterMapData)
633→        : <div>Loading...</div>
634→      break
635→    case 'article':
636→      page = Content
637→        ? renderArticlePage(data.manifest, data.route, Content)
638→        : <div>Loading...</div>
639→      break
640→    case 'books':
641→      page = renderHomePage(data.manifest)
642→      break
643→    case 'articles':
644→      page = renderHomePage(data.manifest)
645→      break
646→    default:
647→      page = <div>Page not found</div>
648→  }
649→
650→  render(page, document.getElementById('app')!)
651→}
