            input[name] = VIRTUAL_ROUTE_PREFIX + route.path
          }

          return {
            appType: 'custom' as const,
            resolve: {
              alias: {
                'preact': dirname(_require.resolve('preact/package.json')),
                'preact/jsx-runtime': dirname(_require.resolve('preact/package.json')) + '/jsx-runtime',
              },
            },
            build: {
