119→// Theme management
120→function resolveSystemTheme(): 'light' | 'dark' {
121→  return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light'
122→}
123→
124→export function setTheme(theme: 'light' | 'dark' | 'sepia' | 'system'): void {
125→  currentTheme.value = theme
126→  localStorage.setItem('pressy-theme', theme)
127→  const resolved = theme === 'system' ? resolveSystemTheme() : theme
128→  document.documentElement.setAttribute('data-theme', resolved)
129→}
130→
131→function loadTheme(): void {
132→  const saved = localStorage.getItem('pressy-theme') as 'light' | 'dark' | 'sepia' | 'system' | null
133→  if (saved) {
134→    setTheme(saved)
135→  } else if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
136→    setTheme('dark')
137→  }
138→
139→  // Listen for OS theme changes so "system" mode updates live
140→  window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', () => {
141→    if (currentTheme.value === 'system') {
142→      document.documentElement.setAttribute('data-theme', resolveSystemTheme())
143→    }
144→  })
145→}
