266→// Calculates global book progress % from word counts + saved progress data
267→function calculateBookProgressPercent(
268→  book: Book,
269→  currentChapterSlug: string,
270→  currentPage: number,
271→  currentTotalPages: number,
272→  allProgress: ReadingProgress[],
273→): number {
274→  const totalWords = book.chapters.reduce((sum, ch) => sum + (ch.wordCount || 0), 0)
275→  if (totalWords === 0) return 0
276→
277→  const progressMap = new Map(allProgress.map((p) => [p.chapterSlug, p]))
278→  let wordsRead = 0
279→
280→  for (const ch of book.chapters) {
281→    const chapterWords = ch.wordCount || 0
282→    if (ch.slug === currentChapterSlug) {
283→      // Use live page position for current chapter
284→      if (currentTotalPages > 0) {
285→        wordsRead += (currentPage / Math.max(1, currentTotalPages - 1)) * chapterWords
286→      }
287→    } else {
288→      const progress = progressMap.get(ch.slug)
289→      if (!progress) continue
290→      if (progress.totalPages > 0 && progress.page >= progress.totalPages - 1) {
291→        wordsRead += chapterWords
292→      } else if (progress.page > 0 && progress.totalPages > 0) {
293→        wordsRead += (progress.page / progress.totalPages) * chapterWords
294→      }
295→    }
296→  }
297→
298→  return Math.min(100, (wordsRead / totalWords) * 100)
299→}
