301→// Wrapper component that provides progress callbacks and computes global progress
302→function ChapterReaderWithProgress({
303→  book,
304→  chapterSlug,
305→  chapter,
306→  prevChapter,
307→  nextChapter,
308→  paginationMode,
309→  Content,
310→  chapterMapData,
311→}: {
312→  book: Book
313→  chapterSlug: string
314→  chapter?: Chapter
315→  prevChapter?: { slug: string; title: string }
316→  nextChapter?: { slug: string; title: string }
317→  paginationMode?: 'scroll' | 'paginated'
318→  Content: ComponentType<{ components?: Record<string, unknown> }>
319→  chapterMapData?: ChapterMapData
320→}) {
321→  const [bookProgressPercent, setBookProgressPercent] = useState<number | undefined>(undefined)
322→
323→  // Load initial global book progress
324→  useEffect(() => {
325→    getAllReadingProgress().then((allProgress) => {
326→      const percent = calculateBookProgressPercent(book, chapterSlug, 0, 0, allProgress)
327→      setBookProgressPercent(percent)
328→    })
329→  }, [book, chapterSlug])
330→
331→  const handleSaveProgress = (data: ProgressData) => {
332→    saveReadingProgress({
333→      chapterSlug,
334→      page: data.page,
335→      totalPages: data.totalPages,
336→      scrollPosition: data.scrollPosition,
337→      timestamp: Date.now(),
338→    })
339→
340→    // Update global book progress with live page position
341→    if (data.totalPages > 0) {
342→      getAllReadingProgress().then((allProgress) => {
343→        const percent = calculateBookProgressPercent(
344→          book, chapterSlug, data.page, data.totalPages, allProgress,
345→        )
346→        setBookProgressPercent(percent)
347→      })
348→    }
349→  }
350→
351→  const handleRestoreProgress = async (): Promise<ProgressData | null> => {
352→    const progress = await getReadingProgress(chapterSlug)
353→    if (!progress) return null
354→    return {
355→      page: progress.page,
356→      totalPages: progress.totalPages,
357→      scrollPosition: progress.scrollPosition,
358→    }
359→  }
360→
361→  const handleChapterChange = (slug: string, page: number, chapterTotalPages: number) => {
362→    // Save progress for the chapter being left (mark as complete)
363→    saveReadingProgress({
364→      chapterSlug: slug,
365→      page,
366→      totalPages: chapterTotalPages,
367→      scrollPosition: 0,
368→      timestamp: Date.now(),
369→    })
370→
371→    // Update global book progress
372→    getAllReadingProgress().then((allProgress) => {
373→      const percent = calculateBookProgressPercent(book, slug, page, chapterTotalPages, allProgress)
374→      setBookProgressPercent(percent)
375→    })
376→  }
377→
378→  // Prefetch previous chapter HTML for fast backward navigation
379→  useEffect(() => {
380→    if (prevChapter) {
381→      const link = document.createElement('link')
382→      link.rel = 'prefetch'
383→      link.href = prevChapter.slug
384→      document.head.appendChild(link)
385→      return () => { document.head.removeChild(link) }
386→    }
387→  }, [prevChapter])
388→
389→  return (
390→    <Reader
391→      title={chapter?.title || chapterSlug}
392→      bookTitle={book.metadata.title}
393→      chapterSlug={chapterSlug}
394→      prevChapter={prevChapter}
395→      nextChapter={nextChapter}
396→      paginationMode={paginationMode}
397→      onSaveProgress={handleSaveProgress}
398→      onRestoreProgress={handleRestoreProgress}
399→      bookProgressPercent={bookProgressPercent}
400→      initialContent={Content}
401→      chapterMapData={chapterMapData}
402→      currentChapterSlug={chapterSlug}
403→      allChapters={book.chapters.map(ch => ({ slug: ch.slug, title: ch.title, wordCount: ch.wordCount }))}
404→      bookBasePath={`${basePath}/books/${book.slug}`}
405→      onChapterChange={handleChapterChange}
406→      mdxComponents={useMDXComponents()}
407→    >
408→      <Content components={useMDXComponents()} />
409→    </Reader>
410→  )
411→}
412→
413→function renderChapterPage(
414→  manifest: ContentManifest,
415→  route: string,
416→  Content: ComponentType,
417→  paginationMode?: 'scroll' | 'paginated',
418→  chapterMapData?: ChapterMapData,
419→) {
420→  const parts = route.split('/')
421→  const bookSlug = parts[2]
422→  const chapterSlug = parts[3]
423→  const book = manifest.books.find((b: Book) => b.slug === bookSlug)
424→  const chapterIdx = book ? book.chapters.findIndex((c: Chapter) => c.slug === chapterSlug) : -1
